package com.uaic.gaitauthentication.ui.home;

import android.content.Intent;
import android.content.SharedPreferences;
import android.graphics.Bitmap;
import android.graphics.BitmapFactory;
import android.net.Uri;
import android.os.Bundle;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.Button;
import android.widget.TextView;

import androidx.annotation.NonNull;
import androidx.fragment.app.Fragment;

import com.auth0.android.jwt.JWT;
import com.mikhaellopez.circularimageview.CircularImageView;
import com.uaic.gaitauthentication.R;
import com.uaic.gaitauthentication.helpers.ImageUtil;

import java.io.FileNotFoundException;
import java.io.InputStream;

import static android.preference.PreferenceManager.getDefaultSharedPreferences;

public class HomeFragment extends Fragment {
    private HomeViewModel homeViewModel;
    private TextView username;
    private TextView token;
    private TextView tokenExpiration;
    private TextView email;
    private TextView profileSteps;
    private Button pictureSelectorButton;
    private CircularImageView profilePicture;
    private final int REQUEST_CODE = 1;
    private final String DEFAULT_IMAGE = "";

    public View onCreateView(@NonNull LayoutInflater inflater,
                             ViewGroup container, Bundle savedInstanceState) {
        View root = inflater.inflate(R.layout.fragment_home, container, false);
        profilePicture = root.findViewById(R.id.profile_picture);
        pictureSelectorButton = root.findViewById(R.id.profile_picture_button);
        username = root.findViewById(R.id.profile_username);
        email = root.findViewById(R.id.profile_email);
        token = root.findViewById(R.id.profile_token);
        tokenExpiration = root.findViewById(R.id.profile_token_expiration);
        profileSteps = root.findViewById(R.id.profile_steps);

        setProfileInfo();

        pictureSelectorButton.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                Intent intent = new Intent(Intent.ACTION_PICK);
                intent.setType("image/*");
                startActivityForResult(intent, REQUEST_CODE);
            }
        });

        return root;
    }

    @Override
    public void onActivityResult(int requestCode, int resultCode, Intent data) {
        super.onActivityResult(requestCode, resultCode, data);

        if (requestCode == REQUEST_CODE) {
            try {
                final Uri imageUri = data.getData();
                final InputStream imageStream = getActivity().getContentResolver().openInputStream(imageUri);
                final Bitmap selectedImage = BitmapFactory.decodeStream(imageStream);

                storeProfilePicture(ImageUtil.convert(selectedImage));

                profilePicture.setImageBitmap(selectedImage);
            } catch (FileNotFoundException e) {
                e.printStackTrace();
            }
        }
    }

    private void setProfileInfo() {
        SharedPreferences preferences = getDefaultSharedPreferences(getContext().getApplicationContext());
        String storedToken = preferences.getString("token", null);
        Long steps = preferences.getLong("profileSteps", 0);
        String defaultImage = preferences.getString("profilePicture" , DEFAULT_IMAGE);

        if (token == null) {
            throw new IllegalArgumentException("Token has not been set!");
        }

        JWT jwt = new JWT(storedToken);
        username.setText(jwt.getClaim("username").asString());
        email.setText(jwt.getSubject());
        token.setText(storedToken.substring(0, 5) + "***");
        tokenExpiration.setText(jwt.getExpiresAt().toString());
        profileSteps.setText(String.valueOf(steps));
        profilePicture.setImageBitmap(ImageUtil.convert(defaultImage));
    }

    private void storeProfilePicture(String base64Image) {
        SharedPreferences.Editor preferences = getDefaultSharedPreferences(getContext().getApplicationContext()).edit();
        preferences.putString("profilePicture", base64Image);
        preferences.commit();
    }

}